import React, { Component } from 'react';
import { AboutMe } from './components/aboutme/aboutme.component';
import {CardList} from './components/classwork-list/classwork-list.component';
import { Route } from 'react-router-dom';
import { Nav, Navbar, Alert } from 'react-bootstrap';

function withProps(Component, props) {
    return function(matchProps) {
      return <Component {...props} {...matchProps} />
    }
  }

class App extends Component {
  constructor() {
    super();
    this.state = {
      classlist: []
    };
  }


  NavigationBar = () => (
    <>
        <Navbar bg="dark" variant="dark">
            <Route exact path='/' component={AboutMe}><Navbar.Brand href="/">Patrick Leduc</Navbar.Brand></Route>
            <Nav className="mr-auto">
                <Route><Nav.Link href="/experience">Experience</Nav.Link></Route>
                <Route><Nav.Link href="/projects">Projects</Nav.Link></Route>
                <Route exact path='/education' component={withProps(CardList, classlist = this.state.classlist)}><Nav.Link href="/education">Education</Nav.Link></Route>
                <Route><Nav.Link href="/contact">Contact</Nav.Link></Route>
                <Route><Nav.Link href="/blog">Blog</Nav.Link></Route>
            </Nav>
        </Navbar>
        <Alert variant="primary">
            <p> This website runs on a Kubernetes Cluster! </p>
        </Alert>
    </>
    );

//when mounted, calls this block of code
  componentDidMount(){
    fetch('http://localhost:8080/classes')
    .then(response => response.json())
    .then(classes => this.setState({classlist:classes}));
  }




    render() {
      return (
        <React.Fragment>
          <NavigationBar />


        </React.Fragment>


      );
    }
  }




export default App;
